import { expect, test } from "vitest";
import * as z from "zod";

import { collectExtraKeys } from "../../lib/schemas.js";

test("collectExtraKeys collects excess object keys", () => {
  const schema = z
    .object({
      name: z.string(),
      age: z.number(),
    })
    .catchall(z.unknown());

  const wrapped = collectExtraKeys(schema, "additional");

  const result = wrapped.parse({
    name: "John",
    age: 30,
    extranum: 42,
    extraobj: { cool: "beans" },
  });

  expect(result).toEqual({
    name: "John",
    age: 30,
    additional: { extranum: 42, extraobj: { cool: "beans" } },
  });
});

